import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import { Button, Card, Spinner, Row, Col } from "react-bootstrap";

export default function ShowList() {
	const [movies, setMovies] = useState(null);

	useEffect(() => {
		async function fetchData() {
			const response = await axios.get(
				`https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_API_KEY}&query=James+Bond`
			);
			setMovies(response.data.results);
		}
		fetchData();
	}, []);

	console.log(movies);

	if (movies === null) {
		return <Spinner animation="grow" variant="secondary" />;
	} else {
		return (
			<Row
				lg={4}
				style={{
					marginLeft: "3%",
					marginRight: "3%",
					marginTop: "2%",
					marginBottom: "2%",
					background:'background-color: #032541 '
				}}
			>
				{movies.map(({ title, overview, poster_path, id }) => {
					if (poster_path === null) {
						return null;
					}
					return (
						<Col key={id} style={{marginTop:'1%'}}>
							<Card
								style={{
									width: "16rem",
									alignContent: "space-around",
									margin: "auto",
								}}
							>
								<Card.Img
									variant="top"
									src={`https://image.tmdb.org/t/p/w500${poster_path}`}
									style={{height:'15rem%'}}
								/>
								<Card.Body style={{height:'8rem'}}>
									<Card.Title>{title}</Card.Title>
								</Card.Body>
							</Card>
						</Col>
					);
				})}
			</Row>
		);
	}
}
