import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import { Button, Card, Spinner, Row, Col } from "react-bootstrap";

export default function ShowList() {
	const [movies, setMovies] = useState(null);

	useEffect(() => {
		async function fetchData() {
			const response = await axios.get(
				`https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_API_KEY}&query=James+Bond`
			);
			setMovies(response.data.results);
		}
		fetchData();
	}, []);

	console.log(movies);

	if (movies === null) {
		return <Spinner animation="grow" variant="secondary" />;
	} else {
		return (
			<Row lg={4} style={{}}>
				{movies.map(({ title, overview, poster_path, id }) => {
					if (poster_path === null) {
						return null;
					}
					return (
						<Col key={id}>
							<div
								style={{
									width: "18rem",
									height: "auto",
									alignContent: "space-around",
									marginBottom: "1rem",
								}}
							>
								<Card style={{ width: "18rem" }}>
									<Card.Img
										variant="top"
										src={`https://image.tmdb.org/t/p/w500${poster_path}`}
									/>
									<Card.Body>
										<Card.Title>{title}</Card.Title>
										<Card.Text>
											Some quick example text to build on the card title and
											make up the bulk of the card's content.
										</Card.Text>
										<Button variant="primary">Go somewhere</Button>
									</Card.Body>
								</Card>
							</div>
						</Col>
					);
				})}
			</Row>
		);
	}
}
