import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import { Button, Card, Spinner } from "react-bootstrap";


export default function ShowList() {

    const [movies, setMovies] = useState(null)

	useEffect(() => {
		async function fetchData() {
			const response = await axios.get(
				`https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_API_KEY}&query=James+Bond`
			);
			setMovies(response)
		}
		fetchData();
	}, []);



	return (
		<div>
			{movies === null?(<Spinner animation="grow" variant="secondary" style={{margin:'auto'}} />):(movies.map(() => {
                return(
                    <Card style={{ width: "18rem" }}>
				<Card.Img variant="top" src="holder.js/100px180" />
				<Card.Body>
					<Card.Title>Card Title</Card.Title>
					<Card.Text>
						Some quick example text to build on the card title and make up the
						bulk of the card's content.
					</Card.Text>
					<Button variant="primary">Go to details</Button>
				</Card.Body>
			</Card>
                )
            }))}
		</div>
	);
}
